const zoomOutIcon = "url(data:image/png;base64,),auto";

const zoomInIcon = "url(data:image/png;base64,),auto";


const iconChange = e => {
    if (e.ctrlKey || e.altKey) {
        document.body.style.cursor = zoomOutIcon;
    }
    else {
        document.body.style.cursor = zoomInIcon;
    }
};

